import * as types from "./actionTypes";

const initialState = {
    contacts: [],
};

const contactReducer = (state = initialState, action) => {
    switch (action.type) {
        case types.GET_CONTACTS:
            return {
                ...state,
                contacts: action.payloa
            }
        default:
            return state;
    }
}

export default contactReducer;
