import { MDBCol, MDBContainer, MDBRow, MDBInput, MDBBtn, MDBTypography } from 'mdb-react-ui-kit';
import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core';
import { useDispatch, useSelector} from "react-redux";
import { addContactInitiate } from './redux/actions';


const initialState ={
    name: "",
    contact: "",
    email: "",
    address: ""
}

const useStyles = makeStyles((theme) => ({
    root: {
        marginTop: 70,
        margin: "auto",
        padding: "15px",
        maxwidth: "500px",
        alignCotent: "center",
        "& > *": {
            margin: theme.spacing(1),
            width: "45ch",
        },
    },
}));

const Home = () => {
const classes = useStyles();
const [state, setStatus] = useState(initialState);
const { name, contact, email, address} = state;
const dispatch = useDispatch();

const handleInputChange = (e) => {
    let { name,value } = e.target;
    setStatus({ ...state, [name]: value});
};
