import { MDBCol, MDBContainer, MDBRow, MDBInput, MDBBtn, MDBTypography, MDBTable, MDBTableHead, MDBTableBody, MDBIcon} from 'mdb-react-ui-kit';
import React, { useState, useEffect} from 'react';
import { makeStyles } from '@material-ui/core';
import { useDispatch, useSelector} from "react-redux";
import { addStudentInitiate, deleteStudentInitiate, getStudentsInitiate } from './redux/actions';


const initialState ={
    name: "",
    age: "",
    gender:"",
    contact: "",
    email: "",
    address: ""
}

const useStyles = makeStyles((theme) => ({
    root: {
        marginTop: 60,
        margin: "auto",
        padding: "15px",
        maxwidth: "500px",
        alignCotent: "center",
        "& > *": {
            margin: theme.spacing(1),
            width: "45ch",
        },
    },
}));

const Home = () => {
const classes = useStyles();
const [state, setStatus] = useState(initialState);
const { name, contact, email, address} = state;
const dispatch = useDispatch();
const {students} = useSelector(state => state.data)

useEffect(() => {
    dispatch(getStudentsInitiate());
}, []);

const deleteStudent = (id) => {
    if(window.confirm("Are you sure that you want to delete ?")){
        dispatch(deleteStudentInitiate(id));
    }
};

const handleInputChange = (e) => {
    let { name,value } = e.target;
    setStatus({ ...state, [name]: value});
};
const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(addStudentInitiate(state));
    setStatus({name:"", age:"", gender:"", contact:"", email:"", address:""});
};
    return (
        <MDBContainer fluid>
            <MDBRow>
            <MDBCol md="4">
                    <form onSubmit={handleSubmit} className={classes.root}>
                        <MDBTypography className="text-start" variant="h4">Add Student</MDBTypography>
                        <MDBInput
                        label="Name"
                        value={name}
                        name="name"
                        type="text"
                        onChange={handleInputChange}
                        /><br/>
                        <MDBInput
                        label="Name"
                        value={name}
                        name="name"
                        type="text"
                        onChange={handleInputChange}
                        /><br/>
                        <MDBInput
                        label="Email"
                        value={email}
                        name="email"
                        type="text"
                        onChange={handleInputChange}
                        /><br/>
                        <MDBInput
                        label="Contact"
                        value={contact}
                        name="contact"
                        type="number"
                        onChange={handleInputChange}
                        /><br/>
                        <MDBInput
                        label="Address"
                        value={address}
                        name="address"
                        type="text"
                        onChange={handleInputChange}
                        /><br/>
                        <MDBBtn style={{width: "100px"}} color="success" type="submit">Submit</MDBBtn>

                    </form>
                </MDBCol>

                <MDBCol md="8">
                    <MDBTable style={{ marginTop: "100px"}} bordered>
                        <MDBTableHead dark>
        <tr>
          <th scope='col'>No</th>
          <th scope='col'>Name</th>
          <th scope='col'>Email</th>
          <th scope='col'>Contact</th>
          <th scope='col'>Address</th>
          <th scope='col'>Action</th>
        </tr>
      </MDBTableHead>
      {students &&
            students.map((item, index) => (
                <MDBTableBody key={index}>
                <tr>
                <th scope='row'>{index+1}</th>
                <td>{item.name}</td>
                <td>{item.email}</td>
                <td>{item.contact}</td>
                <td>{item.address}</td>
                <td>
                    <MDBBtn 
                        className= "m-1" 
                        tag="a" color="none" 
                        style={{color : "#dd4b39"}} 
                        onClick={() => deleteStudent(item.id)}>

                        <MDBIcon fas icon="trash" size="lg" />
                        
                    </MDBBtn>
                </td>
              </tr>
                </MDBTableBody>
            ))}
     
    </MDBTable>
                </MDBCol>

            </MDBRow>
        </MDBContainer>
    )
}

export default Home
